import {useState} from 'react'

const SignupComponent = () => {

  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    error: '',
    loading: false,
    message: '',
    showForm: true
  })

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log("handed submit")
  }

  const handleChange = (e) => {
    console.log(e.target.value)
  }

  const signupForm = () => {
    return (
      <form>
        <div class="form-group" onSubmit={handleSubmit}>
          <label for="exampleInputEmail1">Name</label>
          <input onChange={handleChange} type="text" class="form-control" />
        </div>
        <div class="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input onChange={handleChange} type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input onChange={handleChange} type="password" class="form-control" id="exampleInputPassword1" />
        </div>
        <button type="submit" class="btn btn-primary">Sign Up</button>
      </form>
    )
  }

  return (
    <div class="container">
      <div class="row">
        <div class="col-lg-12 col-md-12 col-sm-12">
          <h1 class="text-center">Sign Up</h1>
          {signupForm()}
        </div>
      </div>
    </div>
  )
}

export default SignupComponent
